'use client';

import clsx from 'clsx';
import { UploadIcon } from 'lucide-react';
import type { ChangeEvent, ComponentPropsWithRef, DragEvent } from 'react';
import { useCallback, useId, useRef } from 'react';

import { FileDropzone } from '@/vibes/soul/form/file-input/file-dropzone';
import { FileItem } from '@/vibes/soul/form/file-input/file-item';
import { Label } from '@/vibes/soul/form/label';
import { Button } from '@/vibes/soul/primitives/button';
import { toast } from '@/vibes/soul/primitives/toaster';

import { FileUploadProvider, useFileUpload } from './store';

export interface UploadOptions {
  onProgress: (file: File, progress: number) => void;
  onSuccess: (file: File) => void;
  onError: (file: File, error: Error) => void;
}

export interface FileState {
  progress: number;
  status: 'idle' | 'uploading' | 'success' | 'error';
  error?: string;
}

export interface FileInputProps extends ComponentPropsWithRef<'input'> {
  id?: string;
  className?: string;
  onUpload?: (files: File[], options: UploadOptions) => Promise<void> | void;
  onFileAccept?: (file: File) => void;
  onFileReject?: (file: File, message: string) => void;
  onFileValidate?: (file: File) => string | null | undefined;
  hideLabel?: boolean;
  message?: string;
  cta?: string;
  hint?: string;
  invalid?: boolean;
  label?: string;
  maxFiles?: number;
  maxSize?: number;
  uploadingLabel?: string;
  successLabel?: string;
  errorLabel?: string;
}

export function FileInput({ ...props }: FileInputProps) {
  return (
    <FileUploadProvider>
      <FileInputInner {...props} />
    </FileUploadProvider>
  );
}

function FileInputInner({
  id,
  hideLabel = false,
  message,
  invalid,
  cta = 'Upload file',
  label = 'File upload',
  hint = 'or drag and drop files here',
  maxFiles,
  maxSize,
  onFileAccept,
  onFileReject,
  onFileValidate,
  onUpload,
  className,
  disabled,
  accept,
  multiple,
  uploadingLabel = 'Uploading',
  successLabel = 'Upload complete',
  errorLabel = 'Error',
  ...props
}: FileInputProps) {
  const inputRef = useRef<HTMLInputElement | null>(null);

  const generatedId = useId();

  const { state, addFiles, removeFile, setProgress, setSuccess, setError } = useFileUpload();

  const updateInputFiles = useCallback(
    (newFiles: File[]) => {
      const inputElement = inputRef.current;

      if (!inputElement) return;

      const dataTransfer = new DataTransfer();

      const existingFiles = Array.from(state.files.keys());

      const allFiles = [...existingFiles, ...newFiles];
      const limitedFiles = maxFiles !== undefined ? allFiles.slice(0, maxFiles) : allFiles;

      limitedFiles.forEach((file) => dataTransfer.items.add(file));

      inputElement.files = dataTransfer.files;

      addFiles(limitedFiles);
    },
    [maxFiles, addFiles, state.files],
  );

  const handleFiles = useCallback(
    (newFiles: File[]) => {
      const filesToUpload: File[] = [];

      const acceptedFiles = new Map<File, FileState>();

      newFiles.forEach((file) => {
        let rejected = false;
        let rejectionReason = '';

        if (onFileValidate) {
          const validationMessage = onFileValidate(file);
          if (validationMessage !== undefined && validationMessage !== null) {
            rejectionReason = validationMessage;
            rejected = true;
          }
        }

        if (!rejected && accept !== undefined) {
          const acceptTypes = accept.split(',').map((t) => t.trim().toLowerCase());

          const fileType = file.type.toLowerCase();
          const fileExtension = `.${file.name.split('.').pop()?.toLowerCase()}`;

          const isAccepted = acceptTypes.some(
            (type) =>
              type === fileType ||
              type === fileExtension ||
              (type.endsWith('/*') && fileType.startsWith(type.slice(0, -1))),
          );

          if (!isAccepted) {
            rejectionReason = 'File type not accepted';
            rejected = true;
          }
        }

        if (!rejected && maxSize !== undefined && file.size > maxSize) {
          rejectionReason = `File size exceeds limit`;
          rejected = true;
        }

        if (rejected) {
          if (onFileReject) {
            onFileReject(file, rejectionReason);
          } else {
            toast.error(`"${file.name}" was rejected`, {
              description: rejectionReason,
            });
          }
        } else {
          onFileAccept?.(file);
          filesToUpload.push(file);
          acceptedFiles.set(file, { progress: 0, status: 'idle' });
        }
      });

      updateInputFiles(Array.from(acceptedFiles.keys()));

      if (filesToUpload.length > 0) {
        if (onUpload) {
          filesToUpload.forEach((file) => {
            setProgress(file, 0);
          });

          void Promise.resolve(
            onUpload(filesToUpload, {
              onProgress: (file, progress) => {
                setProgress(file, progress);
              },
              onSuccess: (file) => {
                setSuccess(file);
              },
              onError: (file, error) => {
                setError(file, error.message);
              },
            }),
          ).catch((error: unknown) => {
            const errorMessage = error instanceof Error ? error.message : 'Upload failed';
            filesToUpload.forEach((file) => {
              setError(file, errorMessage);
            });
          });
        } else {
          filesToUpload.forEach((file) => {
            setSuccess(file);
          });
        }
      }
    },
    [
      accept,
      maxSize,
      onFileAccept,
      onFileReject,
      onFileValidate,
      updateInputFiles,
      onUpload,
      setProgress,
      setSuccess,
      setError,
    ],
  );

  const handleOnChange = useCallback(
    (event: ChangeEvent<HTMLInputElement>) => {
      const newFiles = Array.from(event.target.files ?? []);

      handleFiles(newFiles);
    },
    [handleFiles],
  );

  const handleOnDrop = useCallback(
    (event: DragEvent<HTMLDivElement>) => {
      event.preventDefault();
      const newFiles = Array.from(event.dataTransfer.files);

      handleFiles(newFiles);
    },
    [handleFiles],
  );

  const handleRemoveFile = useCallback(
    (fileToRemove: File) => {
      const input = inputRef.current;

      if (!input?.files) return;

      const dataTransfer = new DataTransfer();
      const currentFiles = Array.from(input.files);

      currentFiles
        .filter((file) => file !== fileToRemove)
        .forEach((file) => dataTransfer.items.add(file));

      input.files = dataTransfer.files;

      removeFile(fileToRemove);
    },
    [removeFile],
  );

  return (
    <div className={clsx(className, '@container')}>
      <Label
        className={clsx(hideLabel ? 'sr-only' : 'mb-2')}
        htmlFor={`${id ?? generatedId}-input`}
      >
        {label}
      </Label>
      <input
        accept={accept}
        aria-describedby={`${id ?? generatedId}-hint${message !== undefined ? ` ${id ?? generatedId}-message` : ''}`}
        className="sr-only"
        id={`${id ?? generatedId}-input`}
        multiple={multiple}
        onChange={handleOnChange}
        ref={inputRef}
        tabIndex={-1}
        type="file"
        {...props}
      />
      <FileDropzone
        disabled={disabled}
        id={id ?? generatedId}
        inputRef={inputRef}
        invalid={invalid}
        onDrop={handleOnDrop}
      >
        <Button
          aria-controls={`${id ?? generatedId}-input`}
          className="pointer-events-none"
          disabled={disabled}
          size="small"
          tabIndex={-1}
          type="button"
          variant="tertiary"
        >
          <UploadIcon className="size-5 text-(--file-input-trigger-icon,var(--foreground))" />
          {cta}
        </Button>
        <p
          className={clsx(
            'font-(family-name:--file-input-dropzone-message-font-family,var(--font-family-body)) text-sm font-normal text-(--file-input-dropzone-message,var(--contrast-500))',
            // Disabled styles
            'data-[disabled]:text-(--file-input-dropzone-message-disabled,color-mix(in_oklab,var(--contrast-500)_70%,transparent))',
          )}
          data-disabled={disabled === true ? '' : undefined}
          id={`${id ?? generatedId}-hint`}
        >
          {hint}
        </p>
      </FileDropzone>
      {message != null && (
        <p
          className="mt-2 font-(family-name:--file-input-message-font-family,var(--font-family-body)) text-sm font-normal text-(--file-input-message,var(--contrast-500))"
          id={`${id ?? generatedId}-message`}
        >
          {message}
        </p>
      )}
      {state.files.size > 0 && (
        <div className="mt-3 grid gap-3" id={`${id ?? generatedId}-list`} role="list">
          {Array.from(state.files.entries()).map(([file]) => {
            return (
              <FileItem
                errorLabel={errorLabel}
                file={file}
                id={id ?? generatedId}
                key={`${file.name}-${file.size}-${file.lastModified}`}
                onRemoveFile={handleRemoveFile}
                successLabel={successLabel}
                uploadingLabel={uploadingLabel}
              />
            );
          })}
        </div>
      )}
    </div>
  );
}

'use client';

import { clsx } from 'clsx';
import { useCallback, useState } from 'react';
import type {
  ClipboardEvent,
  ComponentPropsWithoutRef,
  DragEvent,
  KeyboardEvent,
  ReactNode,
  RefObject,
} from 'react';

export interface FileDropzoneProps extends ComponentPropsWithoutRef<'div'> {
  children?: ReactNode;
  inputRef: RefObject<HTMLInputElement | null>;
  disabled?: boolean;
  invalid?: boolean;
}

export function FileDropzone({
  id,
  children,
  inputRef,
  disabled,
  invalid,
  onDrop,
}: FileDropzoneProps) {
  const [isDragging, setIsDragging] = useState(false);

  const handleOnDrop = useCallback(
    (event: DragEvent<HTMLDivElement>) => {
      setIsDragging(false);
      onDrop?.(event);
    },
    [onDrop],
  );

  const handleOnDragOver = useCallback((event: DragEvent<HTMLDivElement>) => {
    event.preventDefault();
    setIsDragging(true);
  }, []);

  const handleOnDragEnter = useCallback((event: DragEvent<HTMLDivElement>) => {
    event.preventDefault();
    setIsDragging(true);
  }, []);

  const handleOnDragLeave = useCallback((event: DragEvent<HTMLDivElement>) => {
    const relatedTarget = event.relatedTarget;

    if (
      relatedTarget &&
      relatedTarget instanceof Node &&
      event.currentTarget.contains(relatedTarget)
    ) {
      return;
    }

    event.preventDefault();
    setIsDragging(false);
  }, []);

  const handleOnKeyDown = useCallback(
    (event: KeyboardEvent<HTMLDivElement>) => {
      if (event.key === 'Enter' || event.key === ' ') {
        event.preventDefault();
        inputRef.current?.click();
      }
    },
    [inputRef],
  );

  const handleOnPaste = useCallback(
    (event: ClipboardEvent<HTMLDivElement>) => {
      event.preventDefault();
      setIsDragging(false);

      const items = event.clipboardData.items;
      if (items.length === 0) return;

      const pastedFiles: File[] = [];

      for (const item of items) {
        if (item.kind === 'file') {
          const file = item.getAsFile();
          if (file) {
            pastedFiles.push(file);
          }
        }
      }

      if (pastedFiles.length === 0) return;

      const inputElement = inputRef.current;

      if (!inputElement) return;

      const dataTransfer = new DataTransfer();

      for (const file of pastedFiles) {
        dataTransfer.items.add(file);
      }

      inputElement.files = dataTransfer.files;
      inputElement.dispatchEvent(new Event('change', { bubbles: true }));
    },
    [inputRef],
  );

  const handleOnClick = useCallback(() => {
    inputRef.current?.click();
  }, [inputRef]);

  return (
    <div
      aria-controls={`${id}-input ${id}-list`}
      aria-disabled={disabled}
      className={clsx(
        'flex min-h-19 cursor-pointer flex-col items-center justify-center gap-1.5 rounded-lg border-[1.5px] border-dashed border-(--file-input-dropzone-border,var(--contrast-200)) bg-(--file-input-dropzone-background,var(--background)) p-4 transition-colors duration-300 ease-in-out select-none @sm:flex-row',
        // Hover styles
        'hover:border-(--file-input-dropzone-border-hover,var(--foreground)) hover:bg-(--file-input-dropzone-background-hover,var(--contrast-100))',
        // Focus styles
        'focus-visible:ring-2 focus-visible:ring-(--file-input-focus,var(--primary)) focus-visible:ring-offset-2 focus-visible:outline-hidden',
        // Disabled styles
        'data-[disabled]:cursor-not-allowed data-[disabled]:border-(--file-input-dropzone-border-disabled,color-mix(in_oklab,var(--contrast-200)_70%,transparent)) data-[disabled]:bg-(--file-input-dropzone-background-disabled,var(--background)) data-[disabled]:focus-visible:ring-0 data-[disabled]:focus-visible:ring-offset-0 data-[disabled]:focus-visible:outline-none',
        // Invalid styles
        'data-[invalid]:border-(--file-input-dropzone-border-error,var(--error))',
        // Dragging styles
        'data-[dragging]:border-(--file-input-dropzone-border-dragging,var(--foreground)) data-[dragging]:bg-(--file-input-dropzone-background-dragging,var(--success-highlight))',
      )}
      data-disabled={disabled === true ? '' : undefined}
      data-dragging={isDragging ? '' : undefined}
      data-invalid={invalid === true ? '' : undefined}
      id={`${id}-dropzone`}
      onClick={handleOnClick}
      onDragEnter={handleOnDragEnter}
      onDragLeave={handleOnDragLeave}
      onDragOver={handleOnDragOver}
      onDrop={handleOnDrop}
      onKeyDown={handleOnKeyDown}
      onPaste={handleOnPaste}
      role="button"
      tabIndex={0}
    >
      {children}
    </div>
  );
}

'use client';

import { clsx } from 'clsx';
import {
  FileArchive,
  FileAudio,
  FileCode,
  FileCog,
  FileIcon,
  FileImage,
  FileText,
  FileVideo,
  XIcon,
} from 'lucide-react';
import { ComponentPropsWithoutRef, useEffect, useRef } from 'react';

import { FileInputProps } from '@/vibes/soul/form/file-input';
import { Button } from '@/vibes/soul/primitives/button';

import { useFileUpload } from './store';

export interface FileItemProps
  extends ComponentPropsWithoutRef<'div'>,
    Pick<FileInputProps, 'uploadingLabel' | 'successLabel' | 'errorLabel'> {
  file: File;
  onRemoveFile: (file: File) => void;
}

export function FileItem({
  id,
  file,
  onRemoveFile,
  uploadingLabel,
  successLabel,
  errorLabel,
}: FileItemProps) {
  const { state } = useFileUpload();
  const hasAnimatedRef = useRef(false);

  const fileState = state.files.get(file);

  useEffect(() => {
    // Only animate the file item once since
    // re-rendering will reset the animation
    if (fileState && !hasAnimatedRef.current) {
      setTimeout(() => {
        hasAnimatedRef.current = true;
      }, 300);
    }
  }, [fileState]);

  if (!fileState) {
    return null;
  }

  const { progress, status, error } = fileState;

  const { name, size } = file;

  return (
    <div
      aria-describedby={`${id}-status${error != null ? ` ${id}-error` : ''}`.trim()}
      aria-labelledby={`${id}-name`}
      className={clsx(
        'relative flex items-center justify-between gap-2 overflow-hidden rounded-lg border-[1.5px] border-(--file-input-item-border,var(--contrast-200)) p-4',
        // Animation styles
        !hasAnimatedRef.current && 'fade-in-0 slide-in-from-top-2 animate-in',
        // Invalid styles
        'data-[invalid]:border-(--file-input-item-border-error,var(--error))',
      )}
      data-invalid={fileState.status === 'error' ? '' : undefined}
      id={id}
      role="listitem"
    >
      {/* File metadata */}
      <div className="flex min-w-0 flex-1 flex-col">
        <div className="flex-1">
          <div className="flex items-center gap-1 font-(family-name:--file-input-item-name-font-family,var(--font-family-body))">
            {getFileIcon(file)}
            <p
              className="font-sm truncate font-semibold text-(--file-input-item-name,var(--foreground))"
              id={`${id}-name`}
            >
              {name}
            </p>
          </div>
          {status === 'idle' && (
            <p
              className="text-xs font-normal text-(--file-input-item-status,var(--contrast-500))"
              id={`${id}-status`}
            >
              {formatBytes(size)}
            </p>
          )}
          {status === 'uploading' && (
            <p
              className="text-xs font-normal text-(--file-input-item-status,var(--contrast-500))"
              id={`${id}-status`}
            >
              {uploadingLabel}
            </p>
          )}
          {status === 'success' && (
            <p
              className="text-xs font-normal text-(--file-input-item-status,var(--contrast-500))"
              id={`${id}-status`}
            >
              {successLabel}
            </p>
          )}
          {status === 'error' && (
            <p
              className="text-xs font-normal text-(--file-input-item-status-error,var(--error))"
              id={`${id}-error`}
            >
              {error ?? errorLabel}
            </p>
          )}
        </div>
      </div>
      {/* Remove file button */}
      <Button
        aria-controls={id}
        aria-describedby={`${id}-name`}
        onClick={() => onRemoveFile(file)}
        shape="circle"
        size="x-small"
        type="button"
        variant="ghost"
      >
        <XIcon className="size-5 text-(--file-input-item-delete-icon,var(--foreground))" />
      </Button>
      {/* Progress bar */}
      {status === 'uploading' && (
        <div
          aria-labelledby={`${id}-name`}
          aria-valuemax={100}
          aria-valuemin={0}
          aria-valuenow={progress}
          aria-valuetext={`${progress}%`}
          className="absolute bottom-0 left-0 h-1 w-full bg-(--file-input-item-progress,var(--primary)) transition-transform duration-300 ease-linear"
          role="progressbar"
          style={{ transform: `translateX(-${100 - progress}%)` }}
        />
      )}
    </div>
  );
}

function formatBytes(bytes: number) {
  if (bytes === 0) return '0 B';
  const sizes = ['B', 'KB', 'MB', 'GB', 'TB'];
  const i = Math.floor(Math.log(bytes) / Math.log(1024));
  return `${(bytes / 1024 ** i).toFixed(i ? 1 : 0)} ${sizes[i]}`;
}

function getFileIcon(file: File) {
  const type = file.type;

  const extension = file.name.split('.').pop()?.toLowerCase() ?? '';

  if (type.startsWith('image/')) {
    return <FileImage className="size-4 text-(--file-input-mime-icon,var(--contrast-400))" />;
  }

  if (type.startsWith('video/')) {
    return <FileVideo className="size-4 text-(--file-input-mime-icon,var(--contrast-400))" />;
  }

  if (type.startsWith('audio/')) {
    return <FileAudio className="size-4 text-(--file-input-mime-icon,var(--contrast-400))" />;
  }

  if (type.startsWith('text/') || ['txt', 'md', 'rtf', 'pdf'].includes(extension)) {
    return <FileText className="size-4 text-(--file-input-mime-icon,var(--contrast-400))" />;
  }

  if (
    [
      'html',
      'css',
      'js',
      'jsx',
      'ts',
      'tsx',
      'json',
      'xml',
      'php',
      'py',
      'rb',
      'java',
      'c',
      'cpp',
      'cs',
    ].includes(extension)
  ) {
    return <FileCode className="size-4 text-(--file-input-mime-icon,var(--contrast-400))" />;
  }

  if (['zip', 'rar', '7z', 'tar', 'gz', 'bz2'].includes(extension)) {
    return <FileArchive className="size-4 text-(--file-input-mime-icon,var(--contrast-400))" />;
  }

  if (
    ['exe', 'msi', 'app', 'apk', 'deb', 'rpm'].includes(extension) ||
    type.startsWith('application/')
  ) {
    return <FileCog className="size-4 text-(--file-input-mime-icon,var(--contrast-400))" />;
  }

  return <FileIcon className="size-4 text-(--file-input-mime-icon,var(--contrast-400))" />;
}

'use client';

import { createContext, useCallback, useContext, useMemo, useReducer } from 'react';
import type { ReactNode } from 'react';

interface FileState {
  file: File;
  progress: number;
  error?: string;
  status: 'idle' | 'uploading' | 'error' | 'success';
}

interface FileInputState {
  files: Map<File, FileState>;
  dragOver: boolean;
  invalid: boolean;
}

type FileUploadActions =
  | { type: 'ADD_FILES'; files: File[] }
  | { type: 'SET_FILES'; files: File[] }
  | { type: 'SET_PROGRESS'; file: File; progress: number }
  | { type: 'SET_SUCCESS'; file: File }
  | { type: 'SET_ERROR'; file: File; error: string }
  | { type: 'REMOVE_FILE'; file: File }
  | { type: 'SET_DRAG_OVER'; dragOver: boolean }
  | { type: 'SET_INVALID'; invalid: boolean }
  | { type: 'CLEAR' };

const initialState: FileInputState = {
  files: new Map(),
  dragOver: false,
  invalid: false,
};

function fileUploadReducer(state: FileInputState, action: FileUploadActions): FileInputState {
  switch (action.type) {
    case 'ADD_FILES': {
      const newFiles = new Map(state.files);
      action.files.forEach((file) => {
        if (!newFiles.has(file)) {
          newFiles.set(file, {
            file,
            progress: 0,
            status: 'idle',
          });
        }
      });
      return {
        ...state,
        files: newFiles,
      };
    }

    case 'SET_FILES':
      const setFiles = new Map<File, FileState>();

      action.files.forEach((file) => {
        setFiles.set(file, {
          file,
          progress: 0,
          status: 'idle',
        });
      });

      return {
        ...state,
        files: setFiles,
      };

    case 'SET_PROGRESS':
      const updatedFiles = new Map(state.files);
      const fileState = updatedFiles.get(action.file);

      if (fileState) {
        updatedFiles.set(action.file, {
          ...fileState,
          progress: action.progress,
          status: 'uploading',
        });
      }

      return {
        ...state,
        files: updatedFiles,
      };

    case 'SET_SUCCESS':
      const successFiles = new Map(state.files);
      const successFileState = successFiles.get(action.file);

      if (successFileState) {
        successFiles.set(action.file, {
          ...successFileState,
          status: 'success',
          progress: 100,
        });
      }

      return {
        ...state,
        files: successFiles,
      };

    case 'SET_ERROR':
      const errorFiles = new Map(state.files);
      const errorFileState = errorFiles.get(action.file);

      if (errorFileState) {
        errorFiles.set(action.file, {
          ...errorFileState,
          status: 'error',
          error: action.error,
        });
      }

      return {
        ...state,
        files: errorFiles,
      };

    case 'REMOVE_FILE':
      const removeFiles = new Map(state.files);
      removeFiles.delete(action.file);

      return {
        ...state,
        files: removeFiles,
      };

    case 'SET_DRAG_OVER':
      return {
        ...state,
        dragOver: action.dragOver,
      };

    case 'SET_INVALID':
      return {
        ...state,
        invalid: action.invalid,
      };

    case 'CLEAR':
      return initialState;

    default:
      return state;
  }
}

interface FileUploadContextType {
  state: FileInputState;
  addFiles: (files: File[]) => void;
  setFiles: (files: File[]) => void;
  setProgress: (file: File, progress: number) => void;
  setSuccess: (file: File) => void;
  setError: (file: File, error: string) => void;
  removeFile: (file: File) => void;
  clearAll: () => void;
  setDragOver: (dragOver: boolean) => void;
  setInvalid: (invalid: boolean) => void;
}

const FileUploadContext = createContext<FileUploadContextType | undefined>(undefined);

export function FileUploadProvider({ children }: { children: ReactNode }) {
  const [state, dispatch] = useReducer(fileUploadReducer, initialState);

  const addFiles = useCallback((files: File[]) => {
    dispatch({ type: 'ADD_FILES', files });
  }, []);

  const setFiles = useCallback((files: File[]) => {
    dispatch({ type: 'SET_FILES', files });
  }, []);

  const setProgress = useCallback((file: File, progress: number) => {
    dispatch({ type: 'SET_PROGRESS', file, progress });
  }, []);

  const setSuccess = useCallback((file: File) => {
    dispatch({ type: 'SET_SUCCESS', file });
  }, []);

  const setError = useCallback((file: File, error: string) => {
    dispatch({ type: 'SET_ERROR', file, error });
  }, []);

  const removeFile = useCallback((file: File) => {
    dispatch({ type: 'REMOVE_FILE', file });
  }, []);

  const clearAll = useCallback(() => {
    dispatch({ type: 'CLEAR' });
  }, []);

  const setDragOver = useCallback((dragOver: boolean) => {
    dispatch({ type: 'SET_DRAG_OVER', dragOver });
  }, []);

  const setInvalid = useCallback((invalid: boolean) => {
    dispatch({ type: 'SET_INVALID', invalid });
  }, []);

  const contextValue = useMemo(
    () => ({
      state,
      addFiles,
      setFiles,
      setProgress,
      setSuccess,
      setError,
      removeFile,
      clearAll,
      setDragOver,
      setInvalid,
    }),
    [
      state,
      addFiles,
      setFiles,
      setProgress,
      setSuccess,
      setError,
      removeFile,
      clearAll,
      setDragOver,
      setInvalid,
    ],
  );

  return <FileUploadContext.Provider value={contextValue}>{children}</FileUploadContext.Provider>;
}

export function useFileUpload() {
  const context = useContext(FileUploadContext);
  if (context === undefined) {
    throw new Error('useFileUpload must be used within a FileUploadProvider');
  }
  return context;
}
