import { ScrollAreaBar, ScrollAreaRoot, ScrollAreaRootProps } from './components';

/**
 * This component supports various CSS variables for theming. Here's a comprehensive list, along
 * with their default values:
 *
 * ```css
 * :root {
 *   --scroll-area-background: var(--contrast-100);
 *   --scroll-area-thumb: var(--contrast-200);
 * }
 * ```
 */

export type ScrollAreaProps = Pick<ScrollAreaRootProps, 'className' | 'children' | 'orientation'>;

export function ScrollArea({ className, children, orientation }: ScrollAreaRootProps) {
  return (
    <ScrollAreaRoot
      className={className}
      orientation={orientation}
      scrollHideDelay={500}
      type="hover"
    >
      {children}
      <ScrollAreaBar />
    </ScrollAreaRoot>
  );
}

import { ScrollAreaBar, type ScrollAreaBarProps } from './scroll-area-bar';
import { ScrollAreaRoot, type ScrollAreaRootProps } from './scroll-area-root';

export { ScrollAreaRoot, ScrollAreaBar };

export type { ScrollAreaRootProps, ScrollAreaBarProps };

'use client';

import * as ScrollAreaPrimitive from '@radix-ui/react-scroll-area';
import { clsx } from 'clsx';
import type { ComponentProps } from 'react';

export type ScrollAreaBarProps = ComponentProps<typeof ScrollAreaPrimitive.ScrollAreaScrollbar>;

export function ScrollAreaBar({
  className,
  orientation = 'vertical',
  ...props
}: ScrollAreaBarProps) {
  return (
    <ScrollAreaPrimitive.ScrollAreaScrollbar
      className={clsx(
        'data-[state=visible]:animate-in data-[state=hidden]:animate-out data-[state=hidden]:fade-out-0 data-[state=visible]:fade-in-0 flex touch-none bg-transparent p-1 transition-colors select-none',
        orientation === 'vertical' && 'h-full w-3.5 border-l border-l-transparent',
        orientation === 'horizontal' && 'h-3.5 flex-col border-t border-t-transparent',
        className,
      )}
      data-slot="scroll-area-scrollbar"
      orientation={orientation}
      {...props}
    >
      <ScrollAreaPrimitive.ScrollAreaThumb
        className="bg-contrast-200 relative flex-1 rounded-full"
        data-slot="scroll-area-thumb"
      />
    </ScrollAreaPrimitive.ScrollAreaScrollbar>
  );
}

'use client';

import * as ScrollAreaPrimitive from '@radix-ui/react-scroll-area';
import { clsx } from 'clsx';
import type { ComponentProps } from 'react';

import { ScrollAreaBar } from '@/vibes/soul/primitives/scroll-area/components/scroll-area-bar';

export interface ScrollAreaRootProps extends ComponentProps<typeof ScrollAreaPrimitive.Root> {
  orientation?: 'vertical' | 'horizontal' | 'both';
}

export function ScrollAreaRoot({
  className,
  children,
  orientation = 'vertical',
  ...props
}: ScrollAreaRootProps) {
  return (
    <ScrollAreaPrimitive.Root
      className={clsx('relative', className)}
      data-slot="scroll-area-root"
      {...props}
    >
      <ScrollAreaPrimitive.Viewport
        className="focus-visible:outline-primary size-full rounded-[inherit] transition-[color,box-shadow] focus-visible:outline-1 focus-visible:outline-offset-2"
        data-slot="scroll-area-viewport"
      >
        {children}
      </ScrollAreaPrimitive.Viewport>
      {(orientation === 'vertical' || orientation === 'both') && (
        <ScrollAreaBar orientation="vertical" />
      )}
      {(orientation === 'horizontal' || orientation === 'both') && (
        <ScrollAreaBar orientation="horizontal" />
      )}
      <ScrollAreaPrimitive.Corner />
    </ScrollAreaPrimitive.Root>
  );
}
